import React, { useState } from 'react';
import BlogList from './components/Blog/BlogList';
import About from './components/About';
// Assuming BlogPost is a component that displays a specific post

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState('home');

  const renderContent = () => {
    switch (currentView) {
      case 'home':
        return <BlogList />;
      case 'about':
        return <About />;
      case 'blog':
        return <BlogList />; // Or a different component for blog overview
      default:
        return <BlogList />;
    }
  };

  return (
    <div>
      <nav>
        <ul>
          <li onClick={() => setCurrentView('home')}>Home</li>
          <li onClick={() => setCurrentView('about')}>About</li>
          <li onClick={() => setCurrentView('blog')}>Blog</li>
        </ul>
      </nav>
      <div>
        {renderContent()}
      </div>
    </div>
  );
};

export default App;
